layoutApp.controller("LoginPartController",["$scope","$window",function(n,t){n.showPasswordReset=n.showPasswordReset||function(){$("#passwordResetDialog").modal("show")};n.login=n.login||function(n,i,r){if(!n.$valid){alert(OpenIZ.Localization.getString("locale.security.login.invalid"));return}OpenIZ.App.showWait();OpenIZ.Authentication.loginAsync({userName:i,password:r,continueWith:function(n){n==null?alert(OpenIZ.Localization.getString("err_oauth2_invalid_grant")):(window.location.hash==""&&(window.location.hash="#/"),t.location.reload())},onException:function(n){OpenIZ.App.hideWait();typeof n=="string"?alert(n):n.message!=undefined?alert(""+n.message+" - "+n.details):alert(n)}})}}]);