layoutApp.controller('PatientLayoutController', ['$scope', function($scope){
  $scope.patient = JSON.parse(sessionStorage.getItem("Patient"));
  currentDate = new Date();
  dateValue = $scope.patient.DateOfBirth.split("/");
  dateValue[2] = dateValue[2].split(" ", 1);
  dob = new Date(dateValue[2], dateValue[0], dateValue[1]);
  difference = new Date(currentDate-dob);
  $scope.patient.age = {"years": (difference.getFullYear()-1970), "months": (difference.getMonth()+1), "days": difference.getDate()};
  $('#patientInformationCollapse').on('shown.bs.collapse', function () {
     $(".menu-arrow-toggleable").removeClass("glyphicon-menu-down").addClass("glyphicon-menu-up");
  });

  $('#patientInformationCollapse').on('hidden.bs.collapse', function () {
     $(".menu-arrow-toggleable").removeClass("glyphicon-menu-up").addClass("glyphicon-menu-down");
  });

}]);


var layoutApp = angular.module('layout', ['ngMessages', 'ui.bootstrap', 'localization']);

var compareTo = function() {
    return {
        require: "ngModel",
        scope: {
            otherModelValue: "=compareTo"
        },
        link: function(scope, element, attributes, ngModel) {

            ngModel.$validators.compareTo = function(modelValue) {
                return modelValue == scope.otherModelValue;
            };

            scope.$watch("otherModelValue", function() {
                ngModel.$validate();
            });
        }
    };
};

layoutApp.directive("compareTo", compareTo);

layoutApp.controller('TimeoutController', ['$scope', '$window', '$timeout', 'localize', function($scope, $window, $timeout, localize){

$scope.locale = getStrings();
$scope.localize = function(lang)
{
  OpenIZ.Localization.setLocale(lang);
  $scope.locale = getStrings();
  console.log($scope.locale);
  console.log($scope.locale.gender);
}

toastr.options = {"positionClass":"toast-top-center"};
const TIMEOUT_TIME = 3000000;
var timer = $timeout(function(){timeoutWarning()}, TIMEOUT_TIME);
$scope.resetTimeout = function(){
$timeout.cancel(timer);
timer = $timeout(function(){timeoutWarning()}, TIMEOUT_TIME);
if(toastr.options.timeOut == TIMEOUT_TIME){
toastr.clear();
toastr.options = {"positionClass":"toast-top-center",
"timeOut": "5000"};
toastr.success('You\'re back!');
}

}
function timeoutWarning(){
$timeout.cancel(timer);
timer = $timeout(function(){$scope.logout()}, TIMEOUT_TIME);
toastr.options = {"positionClass":"toast-top-center",
"timeOut": TIMEOUT_TIME.toString()};
toastr.error('Tap the screen to let us know you\'re there.', 'Timeout Warning');
}

$scope.logout = function(){
OpenIZ.Authentication.abandonSession();
$window.location.href = "app://openiz.org/applet/org.timr.applet.login/index";
}

}]);
